import React from 'react'

class LikeButton extends React.Component {

    constructor(props) {
        super(props);

        // binding "this"
        this.toggleLike = this.toggleLike.bind(this);
        this.createLike = this.createLike.bind(this);
        this.removeLike = this.removeLike.bind(this)


    }

    componentDidMount() {
        // fetch posts and then set the state
    }

    toggleLike () {
        if (this.props.likeid) {
            this.removeLike();
        } else {
            this.createLike();
        }

    }

    createLike () {
        // fetch POST: /api/post/likes
        const url = '/api/post/likes';
        console.log('create like: ', url);
    }

    removeLike () {
        // fetch DELETE: /api/post/likes/{likeId}
        const url = '/api/post/likes/' + this.props.likeId;
    }

    render() {
        let likeId = this.props.likeId;
        let heartClass = likeId ? 'fas' : 'fa';
        return (
            <button 
                onClick={this.toggleLike}
                aria-label="Like / Unlike">
                <i className={heartClass + 'fa-heart'}></i>
            </button>
        )
    }
}

export default LikeButton;
