import React from 'react'
import {getHeaders} from './utils'

class LikeButton extends React.Component {

    constructor(props) {
        super(props);

        // binding "this"
        this.toggleLike = this.toggleLike.bind(this);
        this.createLike = this.createLike.bind(this);
        this.removeLike = this.removeLike.bind(this)

        this.state = {
            likeId: this.props.likeId
        }

    }

    componentDidMount() {
        // fetch posts and then set the state
    }

    toggleLike () {
        if (this.state.likeId) {
            this.removeLike();
        } else {
            this.createLike();
        }

    }

    createLike () {
        // fetch POST: /api/post/likes
        const url = '/api/posts/likes';
        const postData = {post_id: this.props.postId}
        console.log('create like: ', url);
        fetch(url, {
            headers: getHeaders(),
            method: 'POST',
            body: JSON.stringify(postData)
        }).then(response => response.json())
        .then(data => {
            // this needs to trigger a post redraw
            this.setState({
                likeId: data.id
            })
            console.log(data);
        })
    }

    removeLike () {
        // fetch DELETE: /api/post/likes/{likeId}
        const url = '/api/posts/likes/' + this.state.likeId;
        console.log('remove like: ', url);
        fetch(url, {
            headers: getHeaders(),
            method: 'DELETE'
        }).then(response => response.json())
        .then(data=> {
            // this needs to trigger a posts redraw
            this.setState({
                likeId: undefined
            })
            console.log(data)

        })
    }

    render() {
        let heartClass = this.state.likeId ? 'fas' : 'fa';
        return (
            <button 
                onClick={this.toggleLike}
                aria-label="Like / Unlike">
                <i className={heartClass + ' fa-heart'}></i>
            </button>
        )
    }
}

export default LikeButton;
