import React from 'react'

import {getHeaders} from './utils'

class Posts extends React.Component {

    constructor(props) {
        super(props);
        // initialization code goes here
        this.state = {
            posts: []
        }

        this.getPostsFromServer()
    }

    getPostsFromServer () {
        fetch('/api/posts', {
            headers: getHeaders()
        }).then(response => response.json())
        .then(data => {
            this.setState({
                posts: data
            })
        })
    }

    componentDidMount() {
        // fetch posts and then set the state
    }

    render() {
        return (
            <div id="posts">
