import React from 'react'
import LikeButton from './LikeButton'
import BookmarkButton from './BookmarkButton';
import {getHeaders} from './utils'

class Post extends React.Component {

    constructor(props) {
        super(props);
        // initialization code goes here
        this.state = {
            post: props.model
        }

        this.refreshPostDataFromServer = this.refreshPostDataFromServer.bind(this);
    }

    componentDidMount() {
        // fetch posts and then set the state
    }

    refreshPostDataFromServer () {
        // refetch the post:
        const url = '/api/posts/' + this.state.post.id;
        fetch(url, {
            headers: getHeaders()
        }).then(response => response.json())
        .then(data => {
            //console.log(data);
            this.setState({
                post: data
            })
        })
    }

    render() {
        const post = this.state.post;
        return (
            // <section className="card">
            //     <div className="header">
            //         <h3>{ post.user.username }</h3>
            //         <i className="fa fa-dots"></i>
            //     </div>

            //     <img 
            //         src={ post.image_url } 
            //         alt={'Image posted by ' +  post.user.username } 
            //         width="300" 
            //         height="300" />

            //     <div className="info">
            //         <div>
            //             <LikeButton 
            //                 postId={post.id} 
            //                 likeId={post.current_user_like_id}
            //                 requeryPost={this.requeryPost} />

            //             Additional data / controls go here...
            //         </div>
            //         <p>{ post.caption }</p>
            //     </div>
            // </section> 
            <section 
                className="card"
                key={'post_' + post.id}>
                <div className="header">
                    <h3>{ post.user.username }</h3>
                    <i className="fa fa-dots"></i>
                </div>
                <img src={post.image_url} 
                     alt={'Image posted by ' + post.user.username }
                     width="300"
                     height="300"/>

                <div className="info">
                    <div>
                        <LikeButton 
                            likeId={post.current_user_like_id}
                            postId={post.id}
                            refreshPost={this.refreshPostDataFromServer}/>
                        <BookmarkButton 
                            bookmarkId={post.current_user_bookmark_id}
                            postId={post.id}
                            refreshPost={this.refreshPostDataFromServer}/>
                    </div>
                    <p>{post.caption}</p>

                </div>

            </section>
        )
    }
}

export default Post;
